#ifndef GEO_H
#define GEO_H

namespace Geo
{

template<typename T>
const T pi = 3.14159265358979323846;

template<typename T>
T degToRad(T deg)
{
    return deg * pi<T> / static_cast<T>(180);
}

void geodeticToCartesianSimplified(
        double lat,
        double lon,
        double &x,
        double &y,
        double &z);

void geodeticToCartesian(
        double lat,
        double lon,
        double &x,
        double &y,
        double &z);

} // namespace Geo

#endif // GEO_H
